mapper.select:
select id,d.dep_name name,d.parent_id parentId
from department d 
	where 
	d.parent_id=#{parentId}
	and d.is_del=0 
	and d.is_outtage=0 

		and d.is_show=0

	and d.company_id=#{companyId} 
	order by d.index_no;

select id,d.dep_name text
from department d 
	where d.parent_id=#{parentId}
	and d.is_del=0 
	and d.is_outtage=0

		and d.is_show=0

	and d.company_id=#{companyId} 
	order by d.index_no;

mapper.update:
update department d set 
	d.dep_name=#{depName},
    d.parent_id=#{parentId},
    d.update_time=#{updateTime},
    d.dep_manager=#{depManager},
    d.is_careerline=#{isCareerLine}
where d.id=#{id}
and d.company_id=#{companyId};

mapper.select:
select count(1)
	from department d
	where d.parent_id=#{depId}
	and d.is_del=0
	and d.company_id=#{companyId};

mapper.update:
update department d 
set d.is_del=1,
	d.update_time=#{updateTime}
where d.id=#{depId}
and d.company_id=#{companyId};

mapper.select:
SELECT id deptId,dep_name deptName 
from department 
WHERE dep_name=#{deptName} and is_del=0 and company_id=#{companyId};
