mapper.update:
update position p set p.is_outtage=#{outtageValue}
where p.id=#{posId}
and p.company_id=#{companyId};

update position p set p.is_del=1 
where p.id=#{posId}
and p.company_id=#{companyId};

mapper.sql:
where p.is_del=0
and p.is_outtage=#{isOuttage}
and p.pos_name like #{findKey}
and p.company_id=#{companyId}

mapper.update:
