mapper.sql:
where r.is_del=0
and r.is_outtage=0
and r.company_id=#{companyId}

mapper.select:
select u.login_name loginName from `user` u
left join rel_role_user ru on u.id = ru.user_id
WHERE ru.role_id=#{id};

select id,d.dep_name name,d.parent_id parentId
from department d 
where d.parent_id=#{parentId} and d.is_del=0 and is_outtage=0 and is_show=0 and company_id=#{companyId} order by index_no;

select id,resource_name name,parent_id parentId
from resource r
where parent_id=#{parentId} and is_del=0 and is_outtage=0 and company_id=#{companyId} order by resource_order;
