mapper.select:
select 
	u.id userId,
    u.user_name userName
from user u 
where u.id=#{userId}
and u.company_id=#{companyId};

mapper.update:
update user u set 
	u.password=#{password},
	u.origin_password=#{originPassword}
where u.id=#{userId}
and u.company_id=#{companyId};

mapper.sql:
where u.company_id=d.company_id and
    u.company_id=rdu.company_id and 
    u.company_id=rpu.company_id and
    u.is_show=0 
and u.login_name like #{loginName}
and u.user_name like #{userName}
and u.email1 like #{email1}
and p.id=#{positionId}
and u.is_outtage=#{state}
and d.id=#{departmentId}

mapper.update:
update user u set 
	u.login_name=#{loginName},
    u.user_name=#{userName},
    u.mobile_phone=#{mobilePhone},
    u.email1=#{email1},
    u.update_time=#{updateTime}
where u.id=${userId}
and u.company_id=${companyId};
